import React, { Component } from "react";
import { useEffect } from "react";
import axios from "axios";
import { Table } from "antd";
import { DeleteOutlined, EditOutlined } from "@ant-design/icons";

const RecipeListTable = () => {
  const [recipes, setRecipes] = React.useState([]);
  const [deleteId, setDeleteId] = React.useState();

  useEffect(() => {
    axios.get("/recipes").then((res) => {
      setRecipes(res.data);
    });
  }, []);

  const handleDelete = (e) => {
    console.log("handle Delete", e);

    // axios.delete(`/delete-recipe/${deleteId}`).then((res) => {
    //   console.log(res);
    // });
  };

  const columns = [
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "Rating",
      dataIndex: "rating",
      key: "rating",
    },
    {
      title: "Content",
      dataIndex: "content",
      key: "content",
    },
    {
      title: "Action",
      render: (e) => (
        <div>
          <DeleteOutlined
            className="me-2"
            onClick={() => {
              handleDelete(e);
            }}
          />
          <EditOutlined />
        </div>
      ),
      key: "action",
    },
  ];
  return (
    <React.Fragment>
      <Table
        dataSource={recipes}
        onRow={(record) => {
          return {
            onClick: () => {
              console.log(record.title);
            }, // click row
          };
        }}
        columns={columns}
      />
      ;
    </React.Fragment>
  );
};

export default RecipeListTable;
