import React, { Component } from "react";
import { useEffect } from "react";
import axios from "axios";
import { message, Table } from "antd";
import { DeleteOutlined, EditOutlined } from "@ant-design/icons";

const RecipeListTable = () => {
  const [recipes, setRecipes] = React.useState([]);

  useEffect(() => {
    axios.get("/recipes").then((res) => {
      setRecipes(res.data);
    });
  }, [recipes]);

  const handleDelete = (_id) => {
    console.log("handle Delete", _id);

    axios.delete(`/delete-recipe/${_id}`).then((res) => {
      res.data.type
        ? message.info(res.data.message)
        : message.error("Error in deleting record");
    });
  };

  const columns = [
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "Rating",
      dataIndex: "rating",
      key: "rating",
    },
    {
      title: "Content",
      dataIndex: "content",
      key: "content",
    },
    {
      title: "Action",
      render: (record) => (
        <div>
          <DeleteOutlined
            className="me-2"
            onClick={() => {
              handleDelete(record._id);
            }}
          />
          <EditOutlined />
        </div>
      ),
      key: "action",
    },
  ];
  return (
    <React.Fragment>
      <Table dataSource={recipes} columns={columns} />;
    </React.Fragment>
  );
};

export default RecipeListTable;
