import { Button, Form, Input, Modal } from "antd";
import React from "react";

const SignUp = () => {
  // Modal Handling
  const [isModalVisible, setIsModalVisible] = React.useState(false);
  //form instance
  const [form] = Form.useForm();

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  // Form Handling
  const onFinish = (values) => {
    // form.getFieldValue({
    //   name: 'https://taobao.com/',
    // });
    console.log("Success:", values);
    const data = form.getFieldValue("firstName");
    console.log("data", data);
  };

  //   const onFieldChange = (errorInfo) => {
  //     console.log("Failed:", errorInfo);
  //   };

  return (
    <React.StrictMode>
      <Modal
        visible={isModalVisible}
        form={form}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          name="signUp"
          onFinish={onFinish}
          //   onFieldsChange={onFieldChange}
          autoComplete="off"
          layout="vertical"
        >
          <div className="row">
            <div className="col-6">
              <Form.Item
                label="First Name"
                name="firstName"
                rules={[
                  {
                    required: true,
                    message: "Please input your first name!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Address"
                name="address"
                rules={[
                  {
                    required: true,
                    message: "Please input your address!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Email"
                name="email"
                rules={[
                  {
                    required: true,
                    type: "email",
                    message: "Please input your email!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
            </div>
            <div className="col-6">
              <Form.Item
                label="Last Name"
                name="lastName"
                rules={[
                  {
                    required: true,
                    message: "Please input your last name!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Phone Number"
                name="phoneNumber"
                rules={[
                  {
                    required: true,
                    message: "Please input your Phone Number!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Password"
                name="password"
                rules={[
                  {
                    required: true,
                    message: "Please input your passwrod!",
                  },
                ]}
              >
                <Input />
              </Form.Item>
            </div>
          </div>
          <div>
            <Button onClick={onFinish}>Submit</Button>
          </div>
        </Form>
      </Modal>

      <Button onClick={showModal}>Sign Up</Button>
    </React.StrictMode>
  );
};

export default SignUp;
