import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import { Button } from "antd";

const Index = () => {
  const [recipeDetails, setRecipeDetails] = React.useState([]);

  let { id } = useParams();
  useEffect(() => {
    axios.get(`/recipe/${id}`).then((res) => {
      console.log("RD", res);
      setRecipeDetails(res.data);
    });
  }, []);
  console.log(recipeDetails);
  return (
    <React.Fragment>
      <div className="recipe-details-wrapper shadow">
        <div className="image-holder"></div>
        <div className="content d-flex justify-content-center">
          <div className="text-start w-25 ">
            <h1 className="fw-bold">{recipeDetails.title} </h1>
            <p className="fs-5">
              Total Time:
              <span className="fs-4 ms-1">
                {recipeDetails.totalTimeReq} hours
              </span>
            </p>
            <p className="fs-5">
              Total Cost: <span>{recipeDetails.totalCost}$</span>
            </p>
            <p className="fs-5">
              Popularity : <span>{recipeDetails.rating}</span>
            </p>
            <p className="fs-5">
              Posted by: <span>{recipeDetails.author}</span>
            </p>
            <Link to="/" className="fs-3">
              <Button>Back</Button>
            </Link>
          </div>
          <div className="text-start w-75">
            <div>
              <h2>Ingredients: </h2>
              <p>{recipeDetails.ingredients}</p>
            </div>
            <div>
              <h2>Discription:</h2>
              <p>{recipeDetails.content}</p>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default Index;
