import React from "react";
import { Button } from "antd";
import { NavLink } from "react-router-dom";
import SignUp from "../Pages/Auth/SignUp";
import LogIn from "../Components/Auth/Login";
import { useUserContext } from "../Context/UserContext";

const Layout = (props) => {
  const { isLogin, logout } = useUserContext();
  const [loggedIn, setLoggedIn] = React.useState(isLogin());
  
  console.log(isLogin());

  const handleLogout = () => {
    logout();
  };
  return (
    <React.Fragment>
      <nav className="nav-layout fs-5">
        <ul className="m-0 list-unstyled py-2 d-flex justify-content-center align-items-center">
          <li className=" mx-4">
            <Button>
              <NavLink to="/">Home</NavLink>
            </Button>
          </li>
          <li className="mx-4">
            <Button onClick={handleLogout}>Logout</Button>
            <LogIn />
          </li>
          <li className="mx-4">
            <SignUp />
          </li>
        </ul>
      </nav>
    </React.Fragment>
  );
};

export default Layout;
