import React, { useEffect } from "react";
import { Rate, message } from "antd";
import  axios  from 'axios';

const Recipe = ({ recipe }) => {
  const [rating, setRating] = React.useState(recipe.rating);
  useEffect(() => {
    console.log("called", recipe._id);
    axios.patch(`/rating/${recipe._id}`, {rating:rating}).then(res=>{
      console.log(res);
    });
  }, []);

  const handleRating = (value) => {
    setRating(value);

    message.info("Rating added " + value + " Stars");
  };
  return (
    <React.Fragment>
      <div className="recipe-card shadow">
        <div className="img-holder">{recipe.image}</div>
        <div className="content">
          <h4>{recipe.title}</h4>

          <div className="text-start">
            <Rate value={rating} onChange={handleRating} className="ratings" />
          </div>
          <p>{recipe.content}</p>
          <p className="text-start">
            by <span className="fw-bold">{recipe.author}</span>
          </p>
        </div>
      </div>
    </React.Fragment>
  );
};

export default Recipe;
