import React, { Component } from "react";
import { useEffect } from "react";
import axios from "axios";
import { Table } from "antd";
import { DeleteOutlined, EditOutlined } from "@ant-design/icons";

const RecipeListTable = () => {
  const [recipes, setRecipes] = React.useState([]);
  const [deleteId, setDeleteId] = React.useState();

  useEffect(() => {
    axios.get("/recipes").then((res) => {
      setRecipes(res.data);
    });
  }, []);

  const handleDelete = () => {
    console.log(deleteId);
    // axios.post("/delete-recipe/${_id}").then((res) => {
    //   console.log(res);
    // });
  };

  const columns = [
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "Rating",
      dataIndex: "rating",
      key: "rating",
    },
    {
      title: "Content",
      dataIndex: "content",
      key: "content",
    },
    {
      title: "Action",
      render: () => (
        <div>
          <DeleteOutlined className="me-2" onClick={handleDelete} />
          <EditOutlined />
        </div>
      ),
      key: "action",
    },
  ];
  return (
    <React.Fragment>
      <Table
        dataSource={recipes}
        onRow={(record) => {
          return {
            onClick: () => {
              console.log("row", record._id);
              setDeleteId(record._id);
            }, // click row
          };
        }}
        columns={columns}
      />
      ;
    </React.Fragment>
  );
};

export default RecipeListTable;
